mapper.select:
select author_name
from paper_author
where paper_id = #{paperId}

select citation_id
from paper_citation
where paper_id = #{paperId}

select file_path, file_name, file_size
from paper_additional_file
where paper_id = #{paperId}

select paper_id, paper_title from paper

    paper_id in

        #{item}

select paper_id, paper_title, paper_meeting, paper_date, paper_summary, paper_link, paper_type, paper_publisher_id, paper_publish_time from paper inner join user on paper_publisher_id = user_id where user_account = #{account}
order by paper_date desc
order by paper_date asc
