mapper.update:
update user
set user_permission = 1
where user_id = #{userId}

update user
set delete_time = now()
where user_id = #{userId}

mapper.select:
select user_account, user_name
from user
where user_id = #{userId}

select *
from user
where delete_time IS NULL limit #{offset}
    , #{pageSize};

select count(1)
from user
where delete_time IS NULL;
