mapper.delete:
delete
from user_note
where note_paper_id = #{notePaperId}
  and note_user_id = #{noteUserId}

mapper.select:
select *
from user_note
where note_paper_id = #{notePaperId}
  and note_user_id = #{noteUserId}

mapper.update:
update user_note
set note_content= #{noteContent}
where note_paper_id = #{notePaperId}
  and note_user_id = #{noteUserId}

mapper.select:
select *
from user_note
where note_user_id = #{noteUserId}
limit #{offset}, #{pageSize}
